    public static String EditionListByAuthor = "SELECT DISTINCT \"Edition\".\"id_edition\", \"Edition\".\"category_ed_id\", \"Edition\".\"name\", \"Room\".\"id_room\", \"Stand\".\"id_stand\", \"Edition\".\"bookshelf_id\", \"Library\".\"name\" FROM ((((((\"Edition\" INNER JOIN \"Edition_literature\" ON \"Edition_literature\".\"edition_id\"=\"Edition\".\"id_edition\") INNER JOIN \"Literature\" ON \"Edition_literature\".\"literature_id\"=\"Literature\".\"id_literature\") INNER JOIN \"Bookshelf\" ON \"Edition\".\"bookshelf_id\"=\"Bookshelf\".\"id_bookshelf\") INNER JOIN \"Stand\" ON \"Bookshelf\".\"stand_id\"=\"Stand\".\"id_stand\") INNER JOIN \"Room\" ON \"Stand\".\"room_id\"=\"Room\".\"id_room\") INNER JOIN \"Library\" ON \"Room\".\"library_id\"=\"Library\".\"id_library\") WHERE \"Literature\".\"author\" LIKE ? AND \"Library\".\"id_library\"=? AND \"Edition\".\"id_edition\" NOT IN (SELECT \"Edition\".\"id_edition\" FROM ((((((\"Edition\" INNER JOIN \"Edition_literature\" ON \"Edition_literature\".\"edition_id\"=\"Edition\".\"id_edition\") INNER JOIN \"Literature\" ON \"Edition_literature\".\"literature_id\"=\"Literature\".\"id_literature\") INNER JOIN \"Bookshelf\" ON \"Edition\".\"bookshelf_id\"=\"Bookshelf\".\"id_bookshelf\") INNER JOIN \"Stand\" ON \"Bookshelf\".\"stand_id\"=\"Stand\".\"id_stand\") INNER JOIN \"Room\" ON \"Stand\".\"room_id\"=\"Room\".\"id_room\") INNER JOIN \"Library\" ON \"Room\".\"library_id\"=\"Library\".\"id_library\") INNER JOIN \"Borrowing\" ON \"Edition\".\"id_edition\"=\"Borrowing\".\"edition_id\" WHERE \"Literature\".\"author\" LIKE ? AND \"Library\".\"id_library\"=? AND \"Borrowing\".\"return_real_data\" IS NULL)";
    public static String EditionListByEdition = "SELECT DISTINCT \"Edition\".\"id_edition\", \"Edition\".\"category_ed_id\", \"Edition\".\"name\", \"Room\".\"id_room\", \"Stand\".\"id_stand\", \"Edition\".\"bookshelf_id\", \"Library\".\"name\" FROM (((((\"Edition\" INNER JOIN \"Edition_literature\" ON \"Edition_literature\".\"edition_id\"=\"Edition\".\"id_edition\")  INNER JOIN \"Literature\" ON \"Edition_literature\".\"literature_id\"=\"Literature\".\"id_literature\") INNER JOIN \"Bookshelf\" ON \"Edition\".\"bookshelf_id\"=\"Bookshelf\".\"id_bookshelf\") INNER JOIN \"Stand\" ON \"Bookshelf\".\"stand_id\"=\"Stand\".\"id_stand\") INNER JOIN \"Room\" ON \"Stand\".\"room_id\"=\"Room\".\"id_room\") INNER JOIN \"Library\" ON \"Room\".\"library_id\"=\"Library\".\"id_library\" WHERE \"Edition\".\"name\" LIKE ? AND \"Library\".\"id_library\"= ? AND \"Edition\".\"id_edition\" NOT IN (SELECT \"Edition\".\"id_edition\" FROM ((((((\"Edition\" INNER JOIN \"Edition_literature\" ON \"Edition_literature\".\"edition_id\"=\"Edition\".\"id_edition\") INNER JOIN \"Literature\" ON \"Edition_literature\".\"literature_id\"=\"Literature\".\"id_literature\") INNER JOIN \"Bookshelf\" ON \"Edition\".\"bookshelf_id\"=\"Bookshelf\".\"id_bookshelf\") INNER JOIN \"Stand\" ON \"Bookshelf\".\"stand_id\"=\"Stand\".\"id_stand\") INNER JOIN \"Room\" ON \"Stand\".\"room_id\"=\"Room\".\"id_room\") INNER JOIN \"Library\" ON \"Room\".\"library_id\"=\"Library\".\"id_library\") INNER JOIN \"Borrowing\" ON \"Edition\".\"id_edition\"=\"Borrowing\".\"edition_id\" WHERE \"Edition\".\"name\" LIKE ? AND \"Library\".\"id_library\"= ? AND \"Borrowing\".\"return_real_data\" IS NULL)";
    public static String EditionListByTitle = "SELECT DISTINCT \"Edition\".\"id_edition\", \"Edition\".\"category_ed_id\", \"Edition\".\"name\", \"Room\".\"id_room\", \"Stand\".\"id_stand\", \"Edition\".\"bookshelf_id\", \"Library\".\"name\" FROM (((((\"Edition\" INNER JOIN \"Edition_literature\" ON \"Edition_literature\".\"edition_id\"=\"Edition\".\"id_edition\")  INNER JOIN \"Literature\" ON \"Edition_literature\".\"literature_id\"=\"Literature\".\"id_literature\") INNER JOIN \"Bookshelf\" ON \"Edition\".\"bookshelf_id\"=\"Bookshelf\".\"id_bookshelf\") INNER JOIN \"Stand\" ON \"Bookshelf\".\"stand_id\"=\"Stand\".\"id_stand\") INNER JOIN \"Room\" ON \"Stand\".\"room_id\"=\"Room\".\"id_room\") INNER JOIN \"Library\" ON \"Room\".\"library_id\"=\"Library\".\"id_library\" WHERE \"Literature\".\"title\" LIKE ? AND \"Library\".\"id_library\"= ? AND \"Edition\".\"id_edition\" NOT IN (SELECT DISTINCT \"Edition\".\"id_edition\" FROM ((((((\"Edition\" INNER JOIN \"Edition_literature\" ON \"Edition_literature\".\"edition_id\"=\"Edition\".\"id_edition\") INNER JOIN \"Literature\" ON \"Edition_literature\".\"literature_id\"=\"Literature\".\"id_literature\") INNER JOIN \"Bookshelf\" ON \"Edition\".\"bookshelf_id\"=\"Bookshelf\".\"id_bookshelf\") INNER JOIN \"Stand\" ON \"Bookshelf\".\"stand_id\"=\"Stand\".\"id_stand\") INNER JOIN \"Room\" ON \"Stand\".\"room_id\"=\"Room\".\"id_room\") INNER JOIN \"Library\" ON \"Room\".\"library_id\"=\"Library\".\"id_library\") INNER JOIN \"Borrowing\" ON \"Edition\".\"id_edition\"=\"Borrowing\".\"edition_id\" WHERE \"Literature\".\"title\" LIKE ? AND \"Library\".\"id_library\"= ? AND \"Borrowing\".\"return_real_data\" IS NULL)";
