    public static String ReaderWithEditionPeriodSigh = "SELECT \"Edition\".\"name\", \"Edition_category\".\"name\", \"Library\".\"name\" FROM (((\"Reader\" INNER JOIN \"Borrowing\" ON \"Reader\".\"id_reader\" = \"Borrowing\".\"reader_id\") INNER JOIN \"Edition\" ON \"Borrowing\".\"edition_id\"=\"Edition\".\"id_edition\") INNER JOIN \"Edition_category\" ON \"Edition_category\".\"id_ed_category\"=\"Edition\".\"category_ed_id\") INNER JOIN \"Library\" ON \"Library\".\"id_library\"=\"Borrowing\".\"library_id\" WHERE (\"Reader\".\"name\" = ?) AND (\"Borrowing\".\"library_id\"=\"Reader\".\"library_id\") AND (\"Borrowing\".\"take_data\" BETWEEN ? AND ?)";
