    public static String WorkerList = "SELECT \"name\" FROM \"Worker\"";
    public static String ReaderByWorkerPeriod = "SELECT \"Reader\".\"name\" FROM (\"Reader\" INNER JOIN \"Borrowing\" ON \"Reader\".\"id_reader\" = \"Borrowing\".\"reader_id\") INNER JOIN \"Worker\" ON \"Worker\".\"id_worker\"= \"Borrowing\".\"worker_taken_id\" OR \"Worker\".\"id_worker\"= \"Borrowing\".\"worker_return_id\" WHERE \"Worker\".\"name\" = ? AND ((\"Borrowing\".\"take_data\" BETWEEN ? AND ?) OR (\"Borrowing\".\"return_real_data\" BETWEEN ? AND ?)) GROUP BY \"Reader\".\"name\"";
