    public static String MostPopularLiterature = "SELECT * FROM (SELECT  \"Literature\".\"title\", COUNT(*) FROM ((\"Borrowing\" INNER JOIN \"Edition\" ON \"Borrowing\".\"edition_id\"=\"Edition\".\"id_edition\") INNER JOIN \"Edition_literature\" ON \"Edition_literature\".\"edition_id\"=\"Edition\".\"id_edition\")  INNER JOIN \"Literature\" ON \"Edition_literature\".\"literature_id\"=\"Literature\".\"id_literature\" GROUP BY \"Literature\".\"title\" ORDER BY COUNT(*) DESC) WHERE ROWNUM <= ?";
